import { FormWrapper } from "./FormWrapper";

type UserData = {
  firstName: string;
  lastName: string;
  age: string;
};

type UserFormProps = UserData & {
  updateFields: (fields: Partial<UserData>) => void;
  inputStyle?: React.CSSProperties; // Add inputStyle prop to UserFormProps
};

export function UserForm({
  firstName,
  lastName,
  age,
  updateFields,
  inputStyle, // Pass inputStyle as a prop
}: UserFormProps) {
  return (
    <FormWrapper title="User Details" inputStyle={{ height: "2rem", fontSize:'1rem' }}>
      <label>First Name</label>
      <input
        autoFocus
        required
        type="text"
        value={firstName}
        onChange={(e) => updateFields({ firstName: e.target.value })}
      />
      <label>Last Name</label>
      <input
        required
        type="text"
        value={lastName}
        onChange={(e) => updateFields({ lastName: e.target.value })}
      />
      <label>Age</label>
      <input
        required
        min={1}
        type="number"
        value={age}
        onChange={(e) => updateFields({ age: e.target.value })}
      />
    </FormWrapper>
  );
}
