import { ReactNode, CSSProperties } from "react";

type FormWrapperProps = {
  title: string;
  children: ReactNode;
  inputStyle?: CSSProperties; // Add an optional inputStyle prop
};

export function FormWrapper({ title, children, inputStyle }: FormWrapperProps) {
  return (
    <>
      <h2 style={{ textAlign: "center", margin: 'auto', marginBottom: "2rem" }}>
        {title}
      </h2>
      <div
        style={{
          display: "grid",
          gap: "1rem .5rem",
          justifyContent: "flex-start",
          gridTemplateColumns: "auto minmax(auto, 400px)",
        }}
      >
        {React.Children.map(children, (child) =>
          React.cloneElement(child as React.ReactElement, { style: inputStyle })
        )}
      </div>
    </>
  );
}
